import keys from './lib/key';
import React, {Component} from 'react';

import Card from './Card';
class Main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: null,
      alert: null
    };
  }

  componentDidMount() {
    fetch(`http://localhost:5000/posts`, {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      method: "POST",
      body: JSON.stringify({"client_api_key": keys.blog_post_api_key})

    }).then(Response => {
      Response
        .json()
        .then(res => {
          this.setState({
            posts: res.sort(function (a, b) {
              // Turn your strings into dates, and then subtract them to get a value that is
              // either negative, positive, or zero.
              return new Date(b.date) - new Date(a.date);
            })

          });
        })
    }).catch(() => {
      console.log("error")
      this.setState({
        alert: {
          content: "There was an error loading blog posts. Try again later."
        }
      })
    });

  }

  render() {
    if (this.state.posts !== null) {
      return (
        <div id="App" className="App uk-container uk-transition-toggle uk-width-2-3">
          {(this.state.alert !== null)
            ? (
              <div class="uk-container">
                <div className="uk-alert-danger" uk-alert="true">
                  <a className="uk-alert-close" uk-close="true"></a>
                  <p>{this.state.alert.content}</p>
                </div>
              </div>
            )
            : (
              <div></div>
            )}
          {this
            .state
            .posts
            .map((element, i) => {
              return (<Card key={i} post={element}/>)
            })}
        </div>
      );
    } else {
      return (
        <div className="App uk-transition-toggle">
          {(this.state.alert !== null)
            ? (
              <div class="uk-container">
                <div className="uk-alert-danger" uk-alert="true">
                  <a className="uk-alert-close" uk-close="true"></a>
                  <p>{this.state.alert.content}</p>
                </div>
              </div>
            )
            : (
              <div></div>
            )}
          <center>
            <span uk-spinner="ratio: 4.5"></span>
          </center>
        </div>
      )
    }
  }
}

export default Main;
