import React, {
    Component
} from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { Markdown } from 'react-showdown';
import dateformat from 'dateformat'


class Posts extends Component {
    render() {
        return ( 
            <div> 
                <div className="post uk-card uk-card-hover uk-margin" id={this.props.post.id}>
                    <div className="uk-card-header">
                        <div className="uk-grid-small uk-flex-middle" uk-grid="true">
                            <div className="uk-width-expand">
                                <h3 className="uk-card-title uk-margin-remove-bottom">{this.props.post.title}</h3>
                                <br />
                                <div className="uk-text-meta uk-margin-remove-top">
                                    <div>
                                        <b>
                                            <span uk-icon="user"></span> {this.props.post.author}
                                        </b>
                                    </div>
                                   <div>
                                        <span uk-icon="clock"></span> {this.props.post.date}
                                   </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="uk-card-body">
                        <Markdown markup={this.props.post.content.substring(0,64)+"..."}/>
                    </div>
                    <div className="uk-card-footer">
                        <Link to={'/post/' + this.props.post.id} className="uk-button uk-button-text">Read more</Link>
                    </div>
                </div>
                <hr />
            </div>
        );
    }
}

export default Posts;
