let pushsub = {
    subscribe_to_localstorage: (ls_object, callback) => {
        let old_version = ""
        setInterval(() => {
            if (localStorage.getItem(ls_object) !== old_version) {
                old_version = localStorage.getItem(ls_object)
                callback()
            }


        }, 500)
    }
}

export default pushsub
