import React, {Component} from 'react';
import SHA256 from 'crypto-js/sha256'
import CryptoJS from 'crypto-js'
import keys from "./lib/key"

class Signup extends Component {
    constructor(props) {
        super(props);
        this.formPreventDefault = this
            .formPreventDefault
            .bind(this);
        this.formChange = this
            .formChange
            .bind(this)
    }

    formPreventDefault(event) {
        event.preventDefault();
        this.handleSignIn();
    }
    formChange() {
        let username = document
                .getElementById("username")
                .value
                .length,
            password = document
                .getElementById("password")
                .value
                .length;
        if (username > 0 && password > 0) {
            document
                .getElementById("signIn")
                .removeAttribute("disabled")
        } else {
            document
                .getElementById("signIn")
                .setAttribute("disabled", "true")
        }
    }

    handleSignIn() {
        let username = document
                .getElementById("username")
                .value,
            password = document
                .getElementById("password")
                .value;

    }
    render() {

        return (
            <div className="uk-container">
                <div className="uk-card uk-card-default uk-card-body">
                    <h1>Sign In</h1>
                    <form onSubmit={this.formPreventDefault} onChange={this.formChange}>
                        <div className="uk-margin">
                            <div className="uk-inline">
                                <span className="uk-form-icon" uk-icon="icon: user"></span>
                                <input
                                    className="uk-input uk-form-width-large"
                                    id="username"
                                    type="text"
                                    placeholder="username"/>
                            </div>
                        </div>
                        <div className="uk-margin">
                            <div className="uk-inline">
                                <span className="uk-form-icon uk-form-icon" uk-icon="icon: lock"></span>
                                <input
                                    className="uk-input uk-form-width-large"
                                    type="password"
                                    id="password"
                                    autoComplete="list"
                                    placeholder="password"/>
                            </div>
                        </div>
                        <div className="uk-margin">
                            <div className="uk-inline">
                                <input
                                    id="signIn"
                                    type="submit"
                                    className="uk-input uk-button uk-button-primary uk-form-width-large"
                                    disabled="true"
                                    value="Sign In"/>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
};

export default Signup;
