import env from "./env";
import keys from "./lib/key"

let auth = {
    user: _ => {
        return {
            username: localStorage.getItem("username"),
            token: localStorage.getItem("token")
        }
    },
    isSignedIn: () => {
        return new Promise((resolve, reject) => {
            fetch(`${env.getCurrent().api}checktoken`, {
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                cache: "force-cache",
                method: "POST",
                body: JSON.stringify({
                    "client_api_key": keys.blog_post_api_key,
                    "token": auth.user().token
                })

            }).then(res => {
                reject("Error")
                res.json()
                    .then(res => {
                        console.log(res)
                        res = JSON.parse(res)
                        if (res.type === "sucsess") {
                            resolve({
                                isSignedIn: true
                            })
                        } else if (res.type === "error") {
                            resolve({
                                isSignedIn: false
                            })
                            auth.signOut();
                        } else {
                            reject("Error")
                        }
                    })
            })
        });
    },
    signOut: () => {
        if ((localStorage.getItem("user-signed-in") === "false" ? false : true)) {
            localStorage.clear()
            window.location.reload();
        }

    },
    isAValidUser: () => {

    },
    canBeHere: () => {
        let url = window.location.href;
        let userTypes = {
            "NOT_SIGNED_IN": ["/"],
            "AUTHOR": ["/", "/dashboard/analytics", "/createpost", "/editprofile"],
            "BASIC_USER": ["/", "/editprofile"]
        }
        switch (localStorage.getItem("type")) {
            case "author":
                if (!userTypes.AUTHOR.contains(url)) {
                    window.location = "/";
                } else {
                    return;
                }
                break;
            case "basic_user":
                if (!userTypes.BASIC_USER.contains(url)) {
                    window.location = "/";
                } else {
                    return;
                }
                break;
            case null:
                if (!userTypes.NOT_SIGNED_IN.contains(url)) {
                    window.location = "/";
                } else {
                    return;
                }
                break;
            default:
                if (!userTypes.NOT_SIGNED_IN.contains(url)) {
                    window.location = "/";
                } else {
                    return;
                }
                break;

        }
    }
};

export default auth;
