let pushsub = {
    subscribe_to_localstorage: (object, callback) => {
        let old_version = ""
        setInterval(() => {
            console.log(localStorage.getItem(object), old_version)
            if (localStorage.getItem(object) !== old_version)
                callback()
        }, 500)
    }
}

export default pushsub
