import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Link} from "react-router-dom";
import keys from "./lib/key"

class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userSignedIn: false

        }
        this.handleChange = this
            .handleChange
            .bind(this);
        this.localStorageUpdated = this
            .localStorageUpdated
            .bind(this)

    }
    handleChange(event) {
        if (event.target.value.length > 0) {
            let search = document.getElementById("search")
            search.style.background = "white"
            search.style.padding = "10px 20px"
            search.style.borderRadius = "5px"
            search.style.textAlign = "center"
        } else {
            Array
                .from(document.getElementsByClassName("post"))
                .forEach(element => {
                    element.style.display = "block"
                })
            document
                .getElementById("search")
                .style
                .background = "transparent"
        }

        fetch(`http://localhost:5000/search/${event.target.value}`, {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            method: "POST",
            body: JSON.stringify({"client_api_key": keys.blog_post_api_key})

        }).then(Response => {
            Response
                .json()
                .then(res => {
                    res.forEach(element => {
                        Array
                            .from(document.getElementsByClassName("post"))
                            .forEach(post => {
                                post.style.display = "block"
                                if (post.id !== element.id) {
                                    post.style.display = "none"
                                } else {}
                            })
                    })
                })
        });
    }
    componentDidMount() {
        console.log("Adding event listener for storage")
        if (typeof window !== 'undefined') {

            window.addEventListener('storage', this.localStorageUpdated)
        }
    }
    localStorageUpdated() {
        console.log("updating state from localstorage")
        if (localStorage.getItem("user-signed-in") === true) {

            this.updateState("userSignedIn", true)
        } else {
            this.updateState("userSignedIn", false)
        }
    }
    updateState(state, value) {
        this.setState({state: value})
    }
    render() {
        return (
            <div uk-sticky="sel-target: .uk-navbar-container; cls-active: uk-navbar-sticky">
                <nav
                    className="uk-navbar-container uk-margin"
                    uk-navbar="mode: click"
                    style={{
                    background: "#1c1c1c"
                }}>
                    <div className="uk-navbar-left">

                        <ul className="uk-navbar-nav">
                            <li
                                className="uk-active uk-text-middle"
                                style={{
                                lineHeight: "150%"
                            }}>
                                <Link
                                    to="/"
                                    style={{
                                    color: "rgba(255,255,255,0.8)"
                                }}>
                                    <span className="uk-margin-small-right" uk-icon="icon: home; ratio: 2"></span>Blog</Link>
                            </li>
                        </ul>

                    </div>

                    {(window.location.href.split('/')[3].length === 0)
                        ? (
                            <div className="uk-navbar-center">
                                <div className="uk-navbar-item ">
                                    <form className="uk-search uk-search-navbar">
                                        <span uk-search-icon="true"></span>
                                        <input
                                            className="uk-search-input"
                                            id="search"
                                            type="search"
                                            autoComplete="off"
                                            placeholder="Search..."
                                            onChange={this
                                            .handleChange
                                            .bind(this)}/>
                                    </form>

                                </div>

                            </div>
                        )
                        : (
                            <div></div>
                        )}

                    <div className="uk-navbar-right">
                        {!this.state.userSignedIn
                            ? (
                                <ul className="uk-navbar-nav">

                                    <li className="uk-active">
                                        <a
                                            href="#"
                                            style={{
                                            color: "rgba(255,255,255,0.8)"
                                        }}>Sign In</a>
                                        <div className="uk-navbar-dropdown">
                                            <ul className="uk-nav uk-navbar-dropdown-nav">
                                                <form>
                                                    <div className="uk-margin">
                                                        <div className="uk-inline">
                                                            <span className="uk-form-icon" uk-icon="icon: user"></span>
                                                            <input className="uk-input" type="text"/>
                                                        </div>
                                                    </div>

                                                    <div className="uk-margin">
                                                        <div className="uk-inline">
                                                            <span className="uk-form-icon uk-form-icon-flip" uk-icon="icon: lock"></span>
                                                            <input className="uk-input" type="text"/>
                                                        </div>
                                                    </div>

                                                    <div className="uk-margin">
                                                        <div className="uk-inline">
                                                            <input className="uk-input" type="submit" value="Sign In"/>
                                                        </div>
                                                    </div>
                                                </form>
                                            </ul>
                                        </div>
                                    </li>
                                    <li className="uk-button-primary">
                                        <Link
                                            to="/signup"
                                            style={{
                                            color: "rgba(255,255,255,0.8)"
                                        }}>Sign Up</Link>
                                    </li>

                                </ul>
                            )
                            : (
                                <ul className="uk-navbar-nav">
                                    <li className="uk-active">
                                        <a
                                            href="#"
                                            style={{
                                            color: "rgba(255,255,255,0.8)"
                                        }}>
                                            <span
                                                className="uk-margin-small-right uk-margin-small-right"
                                                uk-icon="icon: user"></span>
                                            [USERNAME]</a>
                                        <div className="uk-navbar-dropdown">
                                            <ul className="uk-nav uk-navbar-dropdown-nav">
                                                {localStorage.getItem("user-type") === "author"
                                                    ? (
                                                        <li>
                                                            <a href="#">
                                                                <span className="uk-margin-small-right" uk-icon="icon: file-edit"></span>
                                                                Create Post</a>
                                                        </li>
                                                    )
                                                    : (
                                                        <span></span>
                                                    )}
                                                <li>
                                                    <a href="#">
                                                        <span className="uk-margin-small-right" uk-icon="icon: settings"></span>
                                                        Edit Profile</a>
                                                </li>
                                                <li className="uk-nav-divider"></li>
                                                <li>
                                                    <a
                                                        href="#"
                                                        style={{
                                                        color: "#f0506e"
                                                    }}>
                                                        <span className="uk-margin-small-right" uk-icon="icon: sign-out"></span>
                                                        Sign Out</a>
                                                </li>
                                            </ul>
                                        </div>
                                    </li>
                                </ul>
                            )}

                    </div>

                </nav>
            </div>

        );
    }
}

export default Navbar;
