import keys from "./lib/key";
import React, {Component} from 'react';
import dateFormat from 'dateformat';

class Com extends Component {
    render() {
        return (
            <article class="uk-comment">
                <header class="uk-comment-header uk-grid-medium uk-flex-middle" uk-grid>
                    <div class="uk-width-expand">
                        <h4 class="uk-comment-title uk-margin-remove">
                            <a class="uk-link-reset" href="#">{this.props.comment.username}</a>
                        </h4>
                    </div>
                </header>
                <div class="uk-comment-body">
                    <p>{this.props.comment.comment}</p>
                </div>
            </article>

        );
    }
}

class Comments extends Component {
    constructor(props) {
        super(props)
        this.state = {
            comments : null
        }
    }

    componentDidMount() {
        console.log(this.props)
        fetch(`http://localhost:5000/comments/${this.props.post}`, {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            method: "POST",
            body: JSON.stringify({"client_api_key": keys.blog_post_api_key})

        }).then(Response => {
            Response
                .json()
                .then(res => {

                    console.log(res)
                });
        });
    }

    render() {
        return (
            <ul class="uk-comment-list"></ul>
        );
    }
}

export default Comments;
